import { useEffect, useState } from "react";
import useAdmin from "../hooks/useUser";
const DocumentationView = () => {
  // const [products, setProducts] = useState([]);

  const{Product,productData}=useAdmin();
useEffect(()=>{
  Product();
},[]);

console.log(productData);


  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3">
          <div
            className="nav flex-column nav-pills"
            id="v-pills-tab"
            role="tablist"
            aria-orientation="vertical"
          >
            <a
              className="nav-link active"
              id="v-pills-productList-tab"
              data-toggle="pill"
              href="#v-pills-productList"
              role="tab"
              aria-controls="v-pills-productList"
              aria-selected="true"
            >
              Product List
            </a>
          </div>
        </div>
        <div className="col-9">
          <div className="tab-content" id="v-pills-tabContent">
            <div
              className="tab-pane fade show active"
              id="v-pills-productList"
              role="tabpanel"
              aria-labelledby="v-pills-productList-tab"
            >
              <h4>Products</h4>
              <hr />
              <div className="row g-3">
                {productData.length > 0 ? (
                  productData.map((product) => (
                    <div className="col-md-4" key={product._id}>
                      <div className="card h-100">
                        <img
                          src={`/uploads/${product.imageUrl}`} // Assumes the image is stored locally in /uploads
                          className="card-img-top"
                          alt={product.name}
                          style={{ height: "200px", objectFit: "cover" }}
                        />
                        <div className="card-body">
                          <h5 className="card-title">{product.name}</h5>
                          <p className="card-text">{product.description}</p>
                          <p className="card-text">
                            <strong>Price:</strong> ${product.price}
                          </p>
                          <p className="card-text">
                            <strong>Stock:</strong> {product.stock}
                          </p>
                          <p className="card-text">
                            <strong>Category:</strong> {product.category}
                          </p>
                          <p className="card-text">
                            <strong>Brand:</strong> {product.brand}
                          </p>
                          <a href={`/product/${product._id}`} className="btn btn-primary">
                            View Details
                          </a>
                        </div>
                      </div>
                    </div>
                  ))
                ) : (
                  <p>No products available</p>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DocumentationView;
