// src/i18n.js
import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';

i18n
  .use(initReactI18next) // Connects i18next with react-i18next
  .init({
    resources: {
      en: {
        translation: {
          "appTitle": "Mahaluxmi Hardwear",
          "signIn": "SignIn",
          "signUp": "SignUp",
          "welcomeMessage": "Hello! Please sign in or sign up.",
          "allPges":"allPges",
          "checkout":"checkout",
          "contactUs":"contactUs",
          "blog":"blog",
          "blogDetail":"blogDetail",
          "buildingMaterial":"buildingMaterial",
          "plumbing":"plumbing",
        },
      },
      hi: {
        translation: {
          "appTitle": "",
          "signIn": "साइन इन करें",
          "signUp": "साइन अप करें",
          "welcomeMessage": "नमस्ते! कृपया साइन इन या साइन अप करें।"
        },
      },
      mr: {
        translation: {
          "appTitle": "माझ्या अॅपमध्ये आपले स्वागत आहे",
          "signIn": "साइन इन करा",
          "signUp": "साइन अप करा",
          "welcomeMessage": "हॅलो! कृपया साइन इन किंवा साइन अप करा."
        },
      },
    },
    lng: "en", // Default language
    fallbackLng: "en", // Fallback language if the selected one is unavailable
    interpolation: {
      escapeValue: false, // React already escapes values
    },
  });

export default i18n;
