          "contactUs": "Contact Us",
          "blog": "Blog",
          "blogDetail": "Blog Detail",
          "buildingMaterial": "Building Material",
          "plumbing": "Plumbing",
          "electronics": "Electronics",
          "addProduct": "Add Product",
          "View": "View Products",
          " Forgotpassword":"  Forgot password",
